from firewall import *
######################################## # Web Application ########################################
smtp = Firewall() smtp.flush() smtp.policy(smtp.INPUT,smtp.ACCEPT) smtp.policy(smtp.OUTPUT,smtp.ACCEPT) smtp.policy(smtp.FORWARD,smtp.ACCEPT) smtp.policy(smtp.POSTROUTING,smtp.ACCEPT) smtp.input().state(('RELATED','ESTABLISHED')).accept() smtp.input().protocol('icmp').accept() smtp.input().interface('-i','lo').accept() smtp.input().protocol('tcp').state('NEW').dport('22').accept() smtp.input().protocol('tcp').state('NEW').dport(('25','110')).accept() #smtp.input().protocol('tcp').dport(('3306','5432')).reject() smtp.input().reject('--reject-with icmp-host-prohibited') smtp.forward().reject('--reject-with icmp-host-prohibited')
for ip in range(53,58): smtp.postrouting().outbound('enp2s0').protocol('tcp').state('NEW').statistic('5').snat('--to-source 173.24.223.'+str(ip)) for ip in range(130,191): smtp.postrouting().outbound('enp2s0').protocol('tcp').state('NEW').statistic('5').snat('--to-source 107.17.40.'+str(ip)) for ip in range(2,63): smtp.postrouting().outbound('enp2s0').protocol('tcp').state('NEW').statistic('5').snat('--to-source 107.18.142.'+str(ip)) for ip in range(130,191): smtp.postrouting().outbound('enp2s0').protocol('tcp').state('NEW').statistic('5').snat('--to-source 146.71.38.'+str(ip)) for ip in range(194,255): smtp.postrouting().outbound('enp2s0').protocol('tcp').state('NEW').statistic('5').snat('--to-source 104.20.164.'+str(ip))
def start(): smtp.start() def stop(): smtp.stop() def restart(): smtp.stop() smtp.start() def show(): smtp.show() def status(): smtp.status() def main(): show() return( 0 )
if __name__ == '__main__': main()
