$ sudo vim /etc/nagios3/conf.d/contacts_nagios2.cfg
############################################################################### # contacts.cfg ###############################################################################
define contact{ contact_name neo alias Neo service_notification_period 24x7 host_notification_period 24x7 service_notification_options w,u,c,r host_notification_options d,r service_notification_commands notify-service-by-email host_notification_commands notify-host-by-email email neo.chen@example.com }
############################################################################### ############################################################################### # # CONTACT GROUPS # ############################################################################### ###############################################################################
# We only have one contact in this simple configuration file, so there is # no need to create more than one contact group.
define contactgroup{ contactgroup_name admins alias Nagios Administrators members root, neo }

