#!/bin/bash # vpn init file for OpenVPN # # chkconfig: - 100 100 # description: OpenVPN is a full-featured SSL VPN solution which can accomodate a wide range of configurations, # including remote access, site-to-site VPNs, WiFi security, # and enterprise-scale remote access solutions with load balancing, failover, # and fine-grained access-controls # as it is designed and optimized for high performance environments. # author: Neo Chen<openunix@163.com> # # processname: $PROG # config: # pidfile: /var/run/openvpn
# source function library . /etc/init.d/functions
PREFIX=/usr/local/openvpn PROG=$PREFIX/sbin/openvpn OPTIONS="-f /usr/local/openvpn/etc/openvpn.conf" USER=daemon RETVAL=0 prog="openvpn"
start() { echo -n $"Starting $prog: " if [ $UID -ne 0 ]; then RETVAL=1 failure else daemon --user=$USER $PROG $OPTIONS RETVAL=$? [ $RETVAL -eq 0 ] && touch /var/lock/subsys/openvpn fi; echo return $RETVAL }
stop() { echo -n $"Stopping $prog: " if [ $UID -ne 0 ]; then RETVAL=1 failure else killproc $PROG RETVAL=$? [ $RETVAL -eq 0 ] && rm -f /var/lock/subsys/openvpn fi; echo return $RETVAL }
reload(){ echo -n $"Reloading $prog: " killproc $PROG -HUP RETVAL=$? echo return $RETVAL }
restart(){ stop start }
condrestart(){ [ -e /var/lock/subsys/openvpn ] && restart return 0 }
case "$1" in start) start ;; stop) stop ;; restart) restart ;; reload) reload ;; condrestart) condrestart ;; status) status openvpn RETVAL=$? ;; *) echo $"Usage: $0 {start|stop|status|restart|condrestart|reload}" RETVAL=1 esac
exit $RETVAL
