section.screen:
curl -L "https://packages.gitlab.com/install/repositories/runner/gitlab-runner/script.rpm.sh" | sudo bash dnf install gitlab-runner
cp /etc/gitlab-runner/config.toml{,.original}
systemctl enable gitlab-runner

#!/bin/bash
cd /usr/local/src/ wget https://mirrors.bfsu.edu.cn/apache/maven/maven-3/3.8.2/binaries/apache-maven-3.8.2-bin.tar.gz tar zxf apache-maven-3.8.2-bin.tar.gz mv apache-maven-3.8.2 /srv/ rm -f /srv/apache-maven ln -s /srv/apache-maven-3.8.2 /srv/apache-maven
alternatives --install /usr/local/bin/mvn apache-maven-3.8.2 /srv/apache-maven-3.8.2/bin/mvn 0


cat << EOF > /etc/docker/daemon.json

{
	"registry-mirrors": [
    	"https://docker.mirrors.ustc.edu.cn",
		"https://registry.docker-cn.com",
		"https://registry.cn-hangzhou.aliyuncs.com",
    	"http://hub-mirror.c.163.com"
	]
}

EOF
