[root@netkiller ~]# dnf install dnf-plugins-core [root@netkiller ~]# dnf copr enable @caddy/caddy [root@netkiller ~]# dnf install caddy
[root@netkiller ~]# systemctl enable caddy [root@netkiller ~]# systemctl start caddy
[root@netkiller ~]# cp /etc/caddy/Caddyfile{,.original}


http://www.netkiller.cn {

    root * /opt/netkiller.cn/www.netkiller.cn
    file_server

    reverse_proxy /api/* 192.168.30.10:8080 {
	    header_up X-Real-IP {http.request.remote.host}
	    header_up X-Forwarded-For {http.request.remote.host}
	}

}


netkiller.cn {
    reverse_proxy * {
        to https://192.168.0.10
        transport http {
            tls_insecure_skip_verify
        }
    }
}

api.netkiller.cn {
    reverse_proxy * {
        to 192.168.10.10:443
        transport http {
            tls
            tls_insecure_skip_verify
        }
    }
}


www.netkiller.cn {
	route /api* {
    	uri strip_prefix /api
		reverse_proxy api.netkiller.cn:8088
	}
}

