			listen

绑定IP地址
			
listen 80; 相当于0.0.0.0:80监听所有接口上的IP地址
listen 192.168.0.1 80;
listen 192.168.0.1:80;
				

配置默认主机 default_server		
			
server {
    listen       80;
    server_name  acc.example.net;
    ...
}

server {
    listen       80  default_server;
    server_name  www.example.org;
    ...
}
			
		


