		return

301 跳转
		
server {
    listen       80;
    server_name  m.example.com;

    location / {
        return 301 $scheme://www.example.com$request_uri; 
    }
}

server {
    listen 80;
    listen 443 ssl;
    server_name www.old-name.com;
    return 301 $scheme://www.new-name.com$request_uri;
}		
		
	


